static uint8_t sh_buffer[SH_WIDTH * SH_HEIGHT / 2];

static void SH_cmdb(uint8_t cmd)
{
	SH_CMD_PROCESS;
	HAL_SPI_Transmit(&sh1122_spi, &cmd, 1, HAL_MAX_DELAY);
}

static void SH_cmdbb(uint8_t cmd_h, uint8_t cmd_l)
{
	SH_CMD_PROCESS;
	HAL_SPI_Transmit(&sh1122_spi, &cmd_h, 1, HAL_MAX_DELAY);
	HAL_Delay(1);
	HAL_SPI_Transmit(&sh1122_spi, &cmd_l, 1, HAL_MAX_DELAY);
	HAL_Delay(1);
}

