static void SH_rst(void)
{
	HAL_GPIO_WritePin(SH_RST_PORT, SH_RST_PIN, GPIO_PIN_RESET);
	HAL_Delay(1);
	HAL_GPIO_WritePin(SH_RST_PORT, SH_RST_PIN, GPIO_PIN_SET);
	HAL_Delay(200);
}

static void SH_clrRAM(void)
{
	SH_DATA_PROCESS;
	HAL_Delay(100);

	for (int i = 0; i < SH_HEIGHT; i++)
		for (int j = 0; j < SH_WIDTH; j++)
			SH_putPixel(j, i, 0x00);

	SH_update();
}

static void SH_displayDateChars(void)
{
	SH_drawString(24, 50, "DAYS", 15);
	SH_drawString(87, 50, "MIN",  15);
	SH_drawString(146, 50, "HRS",  15);
	SH_drawString(207, 50, "SEC",  15);
}

void SH_background(uint8_t color)
{
	for (int i = 0; i < SH_WIDTH; i++)
		for (int j = 0; j < SH_HEIGHT; j++)
			SH_putPixel(i, j, color);

	SH_update();
}

void SH_putPixel(int x, int y, uint8_t color)
{
	if ((x & 0x01) == 0) {
		sh_buffer[(x + y*SH_WIDTH) / 2] &= 0x0F;
		sh_buffer[(x + y*SH_WIDTH) / 2] |= ((color & 0x0F) << 4);
	}
	else {
		sh_buffer[(x + y*SH_WIDTH) / 2] &= 0xF0;
		sh_buffer[(x + y*SH_WIDTH) / 2] |= (color & 0x0F);
	}
}

void SH_update(void)
{

	SH_setRowAdr(0);
	SH_setColumn(0);
	//SH_setDispLine(0);

	SH_DATA_PROCESS;
	HAL_Delay(100);

	SPI_START;
	HAL_SPI_Transmit(&sh1122_spi, sh_buffer, SH_WIDTH * SH_HEIGHT / 2, HAL_MAX_DELAY);
	SPI_STOP;
}

void SH_init(void)
{
	SH_rst();

	//SH_setContrast(200);
	SH_clrRAM();
	SH_setDispPower(1);
	HAL_Delay(100);

	//SH_displayDateChars();
	SH_tmp();
}

void SH_setColumn(uint8_t column)
{
	column &= 0x7F;
	uint8_t col_h = (column >> 4) | 0x10;
	uint8_t col_l = column & 0x0F;

	SPI_START;
	SH_cmdbb(col_h, col_l);
	SPI_STOP;
}

void SH_setDispLine(uint8_t line)
{
	line &= 0x3F;

	SPI_START;
	SH_cmdb(line);
	SPI_STOP;
}

void SH_setContrast(uint8_t contrast)
{
	uint8_t contrast_msb = 0x81;

	SPI_START;
	SH_cmdbb(contrast_msb, contrast);
	SPI_STOP;
}

void SH_setRemap(uint8_t remap)
{
	remap &= 0x01;

	SPI_START;
	SH_cmdb(remap | 0xA0);
	SPI_STOP;
}

void SH_setEntireDisp(uint8_t state)
{
	state &= 0x01;

	SPI_START;
	SH_cmdb(state | 0xA4);
	SPI_STOP;
}

void SH_setDispMode(uint8_t mode)
{
	mode &= 0x01;

	SPI_START;
	SH_cmdb(mode | 0xA6);
	SPI_STOP;
}

void SH_setDispPower(uint8_t power)
{
	power &= 0x01;

	SPI_START;
	SH_cmdb(power | 0xAE);
	SPI_STOP;
}

void SH_setOffset(uint8_t offset)
{
	uint8_t offset_msb = 0xD3;
	offset &= 0x3F;

	SPI_START;
	SH_cmdbb(offset_msb, offset);
	SPI_STOP;
}

void SH_drawHLine(int x0, int y,  int width, uint8_t color)
{
	for (int i = 0; i < width; i++)
		SH_putPixel(i + x0, y, color);
}

void SH_drawVLine(int x,  int y0, int height, uint8_t color)
{
	for (int i = 0; i < height; i++)
		SH_putPixel(x, i + y0, color);
}

void SH_drawRectangle(int x0, int y0, int width, int height, uint8_t color)
{
	SH_drawHLine(x0, y0, width, color);
	SH_drawHLine(x0, y0 + height, width, color);

	SH_drawVLine(x0, y0, height, color);
	SH_drawVLine(x0 + width, y0, height, color);
}

void SH_setRowAdr(uint8_t row)
{
	uint8_t row_msb = 0xB0;
	row &= 0x3F;

	SPI_START;
	SH_cmdbb(row_msb, row);
	SPI_STOP;
}

void SH_drawString(int x, int y, char *s, uint8_t color)
{
	uint8_t i = 0;
	while (*s) {
		if ((*s >= 65) && (*s <= 90))
			SH_drawChar(i + x, y, *s, color);
		i += 8;
		s++;
	}
}

void SH_drawChar(int x, int y, uint8_t ch, uint8_t color)
{
	printf("%d\n", (uint8_t)ch);
	uint8_t width_limit, height_limit;
	uint8_t type;
	uint32_t line;

	if ((ch >= 65) && (ch <= 90)) {
		width_limit = 5;
		height_limit = 8;
		type = LET;
	}
	else {
		width_limit = 16;
		height_limit = 32;
		type = NUM;
	}


	for (uint8_t i = 0; i < width_limit; i++) {
		if (type == NUM) line = DIGITS[ch][i];
		else             line = LETTERS[ch - 65][i];
		for (uint8_t j = 0; j < height_limit; j++) {
			if (line & (1 << j)) SH_putPixel(i + x, j + y, color);
		}
	}
}

void SH_displayTime(int d, uint8_t h, uint8_t m, uint8_t s)
{
	SH_drawChar(20, 10, d / 10, 15);
	SH_drawChar(40, 10, d % 10, 15);

	SH_drawChar(60, 10, 10, 15);

	SH_drawChar(80, 10, h / 10, 15);
	SH_drawChar(100, 10, h % 10, 15);

	SH_drawChar(120, 10, 10, 15);

	SH_drawChar(140, 10, m / 10, 15);
	SH_drawChar(160, 10, m % 10, 15);

	SH_drawChar(180, 10, 10, 15);

	SH_drawChar(200, 10, s / 10, 15);
	SH_drawChar(220, 10, s % 10, 15);
}
